import React, { useEffect, useState } from 'react';
import { getPlacesByCategory } from '../../services/placesService';
import './Transport.css';

export default function Transport() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selected, setSelected] = useState(null);

  useEffect(() => {
    let mounted = true;
    (async () => {
      setLoading(true);
      const data = await getPlacesByCategory('transport');
      if (mounted) {
        setItems(data);
        setLoading(false);
      }
    })();
    return () => (mounted = false);
  }, []);

  return (
    <main className="transport-page">
      <header className="transport-header">
        <h1>Transport</h1>
        <div className="transport-sub">Major transport hubs and routes</div>
      </header>

      <section className="transport-content">
        {loading && <div className="loading">Loading transport items…</div>}
        {!loading && items.length === 0 && <div>No transport items found.</div>}

        {!loading && (
          <div className="items-grid">
            {items.map((s) => (
              <article key={s.id} className="item-card">
                {s.image && <img src={'data:image/jpeg;base64,'} alt={s.name} className="item-thumb" />}
                <div className="item-card-body">
                  <div className="item-row">
                    <div>
                      <h3 className="item-name">{s.name}</h3>
                      <div className="item-address">{s.address}</div>
                      <div className="item-sub">{s.raw?.mode ? `${s.raw.mode}` : ''}</div>
                    </div>
                    <div className="item-actions">
                      <button className="btn-detail" onClick={() => setSelected(s)}>View</button>
                    </div>
                  </div>

                  <div className="item-meta">
                    <div><strong>Operator:</strong> {s.contact || s.raw?.operator}</div>
                    <div><strong>Capacity:</strong> {s.capacity || (s.raw && s.raw.capacity) || '—'}</div>
                  </div>

                  <div className="item-desc">{s.description}</div>
                </div>
              </article>
            ))}
          </div>
        )}
      </section>

      {selected && (
        <div className="modal-overlay" onClick={() => setSelected(null)}>
          <div className="modal-panel" onClick={(e) => e.stopPropagation()}>
            <div className="modal-top">
              <h2>{selected.name}</h2>
              <div><button onClick={() => setSelected(null)} className="btn-close">Close</button></div>
            </div>
            <div className="modal-body">
              <div className="modal-col">
                {selected.image && <img src={'data:image/jpeg;base64,'} alt={selected.name} className="places-large-image" />}
              </div>
              <div className="modal-col">
                <div><strong>Route / Name:</strong> {selected.name}</div>
                <div><strong>Operator:</strong> {selected.contact || selected.raw?.operator}</div>
                <div><strong>Mode:</strong> {selected.raw?.mode || '—'}</div>
                <div><strong>Capacity:</strong> {selected.capacity || selected.raw?.capacity || '—'}</div>
                <div className="places-desc" style={{marginTop:8}}>{selected.description}</div>

                {selected.raw?.locationLink && (
                  <div style={{marginTop:12}}>
                    <a className="btn-detail" href={selected.raw.locationLink} target="_blank" rel="noreferrer">Open Location</a>
                  </div>
                )}

                <h4 style={{marginTop:12}}>Raw Data</h4>
                <pre className="raw-data">{JSON.stringify(selected, null, 2)}</pre>
              </div>
            </div>
          </div>
        </div>
      )}
    </main>
  );
}
